@SpringBootApplication
@EnableRetry
public class Retgh281Application {

	public static void main(String[] args) {
		SpringApplication.run(Retgh281Application.class, args);
	}

	@Bean
	ApplicationRunner runner(RetryableClass ret) {
		return args -> {
			try {
				ret.doIt("ex1");
			}
			catch (Exception ex) {
				System.out.println(ex.getClass().getSimpleName());
			}
			try {
				ret.doIt("ex2");
			}
			catch (Exception ex) {
				System.out.println(ex.getClass().getSimpleName());
			}
		};
	}
}

@Component
class RetryableClass {

	@Retryable(include = Ex1.class, exclude = Ex2.class, rethrow = true)
	public void doIt(String in) throws Exception {
		System.out.println(in);
		if (in.equals("ex1")) {
			throw new Ex1();
		}
		else if (in.equals("ex2")) {
			throw new Ex2();
		}
	}

}

class Ex1 extends Exception {

}

class Ex2 extends Ex1 {

}
