```
@SpringBootTest
class DemoApplicationTests {

	@Autowired
	List<CustomObject> names;
	
	@Test
	void testOrder() {
		Assertions.assertThat(names.stream().map(CustomObject::getName)).containsExactly("first", "second");
	}

}
