```
public Mono<Void> filter(ServerWebExchange exchange, GatewayFilterChain chain) {
        Object chainStartTime = exchange.getAttributes().get("HANDLE_START_TIME");
        long cost1 = System.currentTimeMillis() - (Long) chainStartTime;
        TestStats.add("Point1", cost1);

        //......

        return ServerWebExchangeUtils.cacheRequestBody(exchange, (serverHttpRequest) -> {
                // don't mutate and build if same request object
                if (serverHttpRequest == exchange.getRequest()) {
                        return chain.filter(exchange);
                }
                ServerWebExchange serverWebExchange = exchange.mutate().request(serverHttpRequest).build();
                long cost2 = System.currentTimeMillis() - (Long) chainStartTime;
                TestStats.add("Point2", cost2);
                return chain.filter(serverWebExchange);
        }).doFinally(signalType -> {
                long cost3 = System.currentTimeMillis() - (Long) chainStartTime;
                TestStats.add("Point3", cost5);
